// TodoList Component
function TodoList({ user, onLogout }) {
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [filter, setFilter] = useState('all');

  useEffect(() => {
    fetchTodos();
  }, []);

  const getAuthHeaders = () => {
    const token = localStorage.getItem('token');
    console.log('Getting token from localStorage:', token ? 'Found' : 'Not found');
    
    if (!token) {
      console.log('❌ No token found, redirecting to login');
      onLogout();
      return {};
    }
    
    return {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${token}`
    };
  };

  const fetchTodos = async () => {
    console.log('🔄 Fetching todos...');
    setIsLoading(true);
    setError('');
    
    try {
      const headers = getAuthHeaders();
      console.log('📤 Request headers:', headers);
      
      const response = await fetch('http://localhost:5000/api/todos', {
        method: 'GET',
        headers: headers
      });

      console.log('📥 Response status:', response.status);
      
      if (!response.ok) {
        if (response.status === 401) {
          console.log('❌ 401 Unauthorized - clearing token and logging out');
          localStorage.removeItem('token');
          localStorage.removeItem('user');
          onLogout();
          return;
        }
        throw new Error(`HTTP ${response.status}`);
      }

      const data = await response.json();
      console.log('✅ Todos fetched successfully:', data.length, 'items');
      setTodos(data);
      setError('');
      
    } catch (error) {
      console.error('❌ Fetch todos error:', error);
      if (error.name === 'TypeError' && error.message.includes('Failed to fetch')) {
        setError('Cannot connect to server. Is your backend running on http://localhost:5000?');
      } else {
        setError(error.message || 'Failed to load todos');
      }
    } finally {
      setIsLoading(false);
    }
  };

  const addTodo = async () => {
    if (!newTodo.trim()) {
      setError('Todo text cannot be empty');
      return;
    }

    console.log('➕ Adding todo:', newTodo.trim());
    setIsLoading(true);
    setError('');
    
    try {
      const response = await fetch('http://localhost:5000/api/todos', {
        method: 'POST',
        headers: getAuthHeaders(),
        body: JSON.stringify({ 
          title: newTodo.trim(),
          description: '',
          completed: false 
        })
      });

      if (!response.ok) {
        if (response.status === 401) {
          localStorage.removeItem('token');
          localStorage.removeItem('user');
          onLogout();
          return;
        }
        throw new Error(`HTTP ${response.status}`);
      }

      const data = await response.json();
      console.log('✅ Todo added successfully:', data.title);
      setTodos([...todos, data]);
      setNewTodo('');
      setError('');
      
    } catch (error) {
      console.error('❌ Add todo error:', error);
      setError(error.message || 'Failed to add todo');
    } finally {
      setIsLoading(false);
    }
  };

  const toggleTodo = async (id) => {
    const todo = todos.find(t => t._id === id);
    if (!todo) return;

    try {
      setIsLoading(true);
      const response = await fetch(`http://localhost:5000/api/todos/${id}`, {
        method: 'PUT',
        headers: getAuthHeaders(),
        body: JSON.stringify({ completed: !todo.completed })
      });

      if (!response.ok) {
        if (response.status === 401) {
          localStorage.removeItem('token');
          localStorage.removeUser('user');
          onLogout();
          return;
        }
        throw new Error(`HTTP ${response.status}`);
      }

      const data = await response.json();
      setTodos(todos.map(t => t._id === id ? data : t));
    } catch (error) {
      console.error('❌ Toggle todo error:', error);
      setError('Failed to update todo status');
    } finally {
      setIsLoading(false);
    }
  };

  const deleteTodo = async (id) => {
    const todo = todos.find(t => t._id === id);
    if (!todo || !window.confirm('Are you sure you want to delete this todo?')) return;

    try {
      setIsLoading(true);
      const response = await fetch(`http://localhost:5000/api/todos/${id}`, {
        method: 'DELETE',
        headers: getAuthHeaders()
      });

      if (!response.ok) {
        if (response.status === 401) {
          localStorage.removeItem('token');
          localStorage.removeItem('user');
          onLogout();
          return;
        }
        throw new Error(`HTTP ${response.status}`);
      }

      setTodos(todos.filter(t => t._id !== id));
    } catch (error) {
      console.error('❌ Delete todo error:', error);
      setError('Failed to delete todo');
    } finally {
      setIsLoading(false);
    }
  };

  const filteredTodos = todos.filter(todo => {
    if (filter === 'active') return !todo.completed;
    if (filter === 'completed') return todo.completed;
    return true;
  });

  const activeCount = todos.filter(todo => !todo.completed).length;
  const completedCount = todos.filter(todo => todo.completed).length;

  return (
    <div className="max-w-4xl mx-auto p-6 bg-white rounded-lg shadow-lg">
      {/* Header */}
      <div className="flex justify-between items-center mb-6">
        <div>
          <h2 className="text-2xl font-bold text-gray-800">My Todos</h2>
          <p className="text-sm text-gray-600">Welcome back, {user?.username}!</p>
        </div>
        <div className="flex items-center gap-4">
          <div className="flex gap-4 text-sm text-gray-600">
            <span className="bg-blue-100 px-2 py-1 rounded">Active: {activeCount}</span>
            <span className="bg-green-100 px-2 py-1 rounded">Completed: {completedCount}</span>
          </div>
          <button
            onClick={onLogout}
            className="px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600 text-sm"
          >
            Logout
          </button>
        </div>
      </div>

      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}

      {/* Add Todo */}
      <div className="flex gap-2 mb-6">
        <input
          type="text"
          value={newTodo}
          onChange={(e) => setNewTodo(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && addTodo()}
          placeholder="Add a new todo..."
          className="flex-1 px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          disabled={isLoading}
        />
        <button 
          onClick={addTodo}
          className="px-6 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600 disabled:opacity-50"
          disabled={isLoading}
        >
          {isLoading ? 'Adding...' : 'Add'}
        </button>
      </div>

      {/* Filter Buttons */}
      <div className="flex gap-2 mb-6">
        {['all', 'active', 'completed'].map(filterType => (
          <button
            key={filterType}
            onClick={() => setFilter(filterType)}
            className={`px-4 py-2 rounded-md ${
              filter === filterType 
                ? 'bg-blue-500 text-white' 
                : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
            }`}
          >
            {filterType.charAt(0).toUpperCase() + filterType.slice(1)}
          </button>
        ))}
      </div>

      {/* Loading State */}
      {isLoading && todos.length === 0 && (
        <div className="text-center py-8 text-gray-500">Loading todos...</div>
      )}

      {/* Todos List */}
      <div className="space-y-2">
        {filteredTodos.length === 0 ? (
          <div className="text-center py-8 text-gray-500">
            {filter === 'all' ? 'No todos yet. Add one above!' : 
             filter === 'active' ? 'No active todos.' : 
             'No completed todos.'}
          </div>
        ) : (
          filteredTodos.map(todo => (
            <div 
              key={todo._id} 
              className={`flex items-center gap-3 p-3 border rounded-md ${
                todo.completed ? 'bg-gray-50 opacity-75' : 'bg-white'
              }`}
            >
              <input
                type="checkbox"
                checked={todo.completed}
                onChange={() => toggleTodo(todo._id)}
                className="w-4 h-4 text-blue-600"
                disabled={isLoading}
              />
              
              <span 
                className={`flex-1 cursor-pointer ${
                  todo.completed ? 'line-through text-gray-500' : 'text-gray-800'
                }`}
                onClick={() => toggleTodo(todo._id)}
              >
                {todo.title}
              </span>

              <button
                onClick={() => deleteTodo(todo._id)}
                className="px-3 py-1 text-red-600 hover:bg-red-50 rounded disabled:opacity-50"
                disabled={isLoading}
              >
                Delete
              </button>
            </div>
          ))
        )}
      </div>

      {/* Debug Info */}
      <div className="mt-8 p-4 bg-gray-100 rounded-md text-xs">
        <h4 className="font-semibold mb-2">Debug Info:</h4>
        <p>Token in localStorage: {localStorage.getItem('token') ? '✅ Present' : '❌ Missing'}</p>
        <p>User in localStorage: {localStorage.getItem('user') ? '✅ Present' : '❌ Missing'}</p>
        <p>Current user: {user?.username || 'Not set'}</p>
      </div>
    </div>
  );
}

// Main App Component
function App() {
  const [user, setUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Check if user is already logged in
    checkAuthStatus();
  }, []);

  const checkAuthStatus = () => {
    console.log('🔍 Checking authentication status...');
    
    const token = localStorage.getItem('token');
    const storedUser = localStorage.getItem('user');
    
    if (token && storedUser) {
      try {
        const parsedUser = JSON.parse(storedUser);
        console.log('✅ Found existing authentication:', parsedUser.username);
        setUser(parsedUser);
        setIsAuthenticated(true);
      } catch (error) {
        console.log('❌ Invalid stored user data, clearing...');
        localStorage.removeItem('token');
        localStorage.removeItem('user');
      }
    } else {
      console.log('❌ No existing authentication found');
    }
    
    setIsLoading(false);
  };

  const handleLogin = (userData, token) => {
    console.log('🔐 Login successful:', userData.username);
    setUser(userData);
    setIsAuthenticated(true);
  };

  const handleLogout = () => {
    console.log('🚪 Logging out...');
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
    setIsAuthenticated(false);
  };

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-gray-900 mx-auto"></div>
          <p className="mt-2 text-gray-600">Loading...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {isAuthenticated ? (
        <TodoList user={user} onLogout={handleLogout} />
      ) : (
        <Login onLogin={handleLogin} />
      )}
    </div>
  );
}

export default App;
