import React, { useState, useEffect } from 'react';
import { todoAPI } from '../services/api';
import TodoItem from './TodoItem';

const TodoList = () => {
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState({
    title: '',
    description: ''
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [creating, setCreating] = useState(false);

  useEffect(() => {
    fetchTodos();
  }, []);

  const fetchTodos = async () => {
    try {
      setLoading(true);
      const todosData = await todoAPI.getTodos();
      setTodos(todosData);
    } catch (error) {
      setError('Failed to fetch todos');
      console.error('Fetch todos error:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    setNewTodo({
      ...newTodo,
      [e.target.name]: e.target.value
    });
    setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!newTodo.title.trim()) {
      setError('Todo title is required');
      return;
    }

    setCreating(true);
    setError('');

    try {
      const createdTodo = await todoAPI.createTodo(newTodo);
      setTodos([...todos, createdTodo]);
      setNewTodo({ title: '', description: '' });
    } catch (error) {
      setError(error.response?.data?.error || 'Failed to create todo');
    } finally {
      setCreating(false);
    }
  };

  const handleUpdateTodo = async (id, updates) => {
    try {
      const updatedTodo = await todoAPI.updateTodo(id, updates);
      setTodos(todos.map(todo => 
        todo.id === id ? updatedTodo : todo
      ));
    } catch (error) {
      setError('Failed to update todo');
      console.error('Update todo error:', error);
    }
  };

  const handleDeleteTodo = async (id) => {
    try {
      await todoAPI.deleteTodo(id);
      setTodos(todos.filter(todo => todo.id !== id));
    } catch (error) {
      setError('Failed to delete todo');
      console.error('Delete todo error:', error);
    }
  };

  if (loading) {
    return <div className="loading">Loading todos...</div>;
  }

  return (
    <div className="todo-container">
      {error && <div className="error-message" id="todo-error">{error}</div>}
      
      {/* Add Todo Form */}
      <div className="todo-form">
        <h2>Add New Todo</h2>
        <form onSubmit={handleSubmit}>
          <div className="todo-input-group">
            <input
              type="text"
              id="todo-title"
              name="title"
              placeholder="Todo title"
              value={newTodo.title}
              onChange={handleInputChange}
              disabled={creating}
            />
            <textarea
              id="todo-description"
              name="description"
              placeholder="Todo description (optional)"
              value={newTodo.description}
              onChange={handleInputChange}
              disabled={creating}
            />
            <button 
              type="submit" 
              className="add-btn"
              id="add-todo-btn"
              disabled={creating || !newTodo.title.trim()}
            >
              {creating ? 'Adding...' : 'Add Todo'}
            </button>
          </div>
        </form>
      </div>

      {/* Todos List */}
      <div className="todos-list">
        <h2>Your Todos ({todos.length})</h2>
        {todos.length === 0 ? (
          <div className="no-todos">No todos yet. Add one above!</div>
        ) : (
          todos.map(todo => (
            <TodoItem
              key={todo.id}
              todo={todo}
              onUpdate={handleUpdateTodo}
              onDelete={handleDeleteTodo}
            />
          ))
        )}
      </div>
    </div>
  );
};

export default TodoList;
