import React, { useState, useEffect } from 'react';
import './TodoList.css';

function TodoList() {
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [filter, setFilter] = useState('all');
  const [editId, setEditId] = useState(null);
  const [editText, setEditText] = useState('');

  useEffect(() => {
    fetchTodos();
  }, []);

  const getAuthHeaders = () => {
    const token = localStorage.getItem('token');
    if (!token) {
      console.log('No token found, redirecting to login...');
      window.location.href = '/login';
      return null;
    }
    return {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${token}`
    };
  };

  const refreshToken = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) return null;

      console.log('Attempting to refresh token...');
      const response = await fetch('http://localhost:5000/api/auth/refresh', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        localStorage.setItem('token', data.token);
        if (data.user) {
          localStorage.setItem('user', JSON.stringify(data.user));
        }
        console.log('Token refreshed successfully');
        return data.token;
      }
      
      console.log('Token refresh failed with status:', response.status);
      return null;
    } catch (error) {
      console.error('Token refresh error:', error);
      return null;
    }
  };

  const makeAuthenticatedRequest = async (url, options = {}) => {
    try {
      // Get initial headers
      let headers = getAuthHeaders();
      if (!headers) return null;
      
      console.log(`Making request to: ${url}`);
      
      // First attempt with current token
      let response = await fetch(url, {
        ...options,
        headers: { ...headers, ...options.headers }
      });

      console.log(`Response status: ${response.status}`);

      // If unauthorized, try to refresh token and retry
      if (response.status === 401) {
        console.log('Received 401, attempting to refresh token...');
        const newToken = await refreshToken();
        
        if (newToken) {
          console.log('Retrying request with new token...');
          headers = {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${newToken}`
          };
          
          response = await fetch(url, {
            ...options,
            headers: { ...headers, ...options.headers }
          });
          
          console.log(`Retry response status: ${response.status}`);
        } else {
          console.log('Token refresh failed, clearing auth and redirecting...');
          localStorage.removeItem('token');
          localStorage.removeItem('user');
          window.location.href = '/login';
          return null;
        }
      }

      return response;
    } catch (error) {
      console.error('Request error:', error);
      throw error;
    }
  };

  const fetchTodos = async () => {
    setIsLoading(true);
    setError('');
    
    try {
      console.log('Fetching todos...');
      const response = await makeAuthenticatedRequest('http://localhost:5000/api/todos');
      
      if (!response) {
        console.log('No response received (likely redirected to login)');
        return;
      }

      const text = await response.text();
      console.log('Raw response:', text);

      let data;
      try {
        data = JSON.parse(text);
      } catch (e) {
        console.error('Failed to parse response as JSON:', e);
        throw new Error('Invalid response from server');
      }
      
      if (!response.ok) {
        console.error('Response not OK:', data);
        throw new Error(data.message || `Server error: ${response.status}`);
      }

      console.log('Todos fetched successfully:', data);
      setTodos(Array.isArray(data) ? data : []);
      setError('');
    } catch (error) {
      console.error('Fetch error:', error);
      setError(error.message || 'Failed to load todos. Please check if the server is running.');
    } finally {
      setIsLoading(false);
    }
  };

  const addTodo = async (e) => {
    e.preventDefault();
    if (!newTodo.trim()) {
      setError('Todo text cannot be empty');
      return;
    }

    setIsLoading(true);
    setError('');

    try {
      console.log('Adding new todo:', newTodo);
      const response = await makeAuthenticatedRequest('http://localhost:5000/api/todos', {
        method: 'POST',
        body: JSON.stringify({ 
          title: newTodo.trim(),
          description: '',
          completed: false 
        })
      });

      if (!response) return;

      const data = await response.json();
      
      if (!response.ok) {
        throw new Error(data.message || 'Failed to add todo');
      }

      console.log('Todo added successfully:', data);
      setTodos([...todos, data]);
      setNewTodo('');
      setError('');
    } catch (error) {
      console.error('Add todo error:', error);
      setError(error.message || 'Failed to add todo. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const startEdit = (todo) => {
    setEditId(todo._id);
    setEditText(todo.title);
  };

  const cancelEdit = () => {
    setEditId(null);
    setEditText('');
  };

  const saveEdit = async (id) => {
    if (!editText.trim()) {
      setError('Todo text cannot be empty');
      return;
    }

    setIsLoading(true);
    setError('');

    try {
      const response = await makeAuthenticatedRequest(`http://localhost:5000/api/todos/${id}`, {
        method: 'PUT',
        body: JSON.stringify({ 
          title: editText.trim()
        })
      });

      if (!response) return;

      const data = await response.json();
      
      if (!response.ok) {
        throw new Error(data.message || 'Failed to update todo');
      }

      setTodos(todos.map(t => t._id === id ? data : t));
      setEditId(null);
      setEditText('');
      setError('');
    } catch (error) {
      console.error('Update error:', error);
      setError(error.message || 'Failed to update todo. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const toggleTodo = async (id) => {
    const todo = todos.find(t => t._id === id);
    if (!todo) return;

    setError('');

    try {
      const response = await makeAuthenticatedRequest(`http://localhost:5000/api/todos/${id}`, {
        method: 'PUT',
        body: JSON.stringify({ completed: !todo.completed })
      });

      if (!response) return;

      const data = await response.json();
      
      if (!response.ok) {
        throw new Error(data.message || 'Failed to update todo');
      }

      setTodos(todos.map(t => t._id === id ? data : t));
    } catch (error) {
      console.error('Toggle error:', error);
      setError(error.message || 'Failed to update todo status.');
    }
  };

  const deleteTodo = async (id) => {
    if (!window.confirm('Are you sure you want to delete this todo?')) return;

    setError('');

    try {
      const response = await makeAuthenticatedRequest(`http://localhost:5000/api/todos/${id}`, {
        method: 'DELETE'
      });

      if (!response) return;

      if (!response.ok) {
        const data = await response.json();
        throw new Error(data.message || 'Failed to delete todo');
      }

      setTodos(todos.filter(t => t._id !== id));
    } catch (error) {
      console.error('Delete error:', error);
      setError(error.message || 'Failed to delete todo. Please try again.');
    }
  };

  const filteredTodos = todos.filter(todo => {
    if (filter === 'active') return !todo.completed;
    if (filter === 'completed') return todo.completed;
    return true;
  });

  const activeCount = todos.filter(todo => !todo.completed).length;
  const completedCount = todos.filter(todo => todo.completed).length;

  return (
    <div className="todo-list-container">
      <div className="todo-header">
        <h2>My Todos</h2>
        <div className="todo-stats">
          <span>Active: {activeCount}</span>
          <span>Completed: {completedCount}</span>
        </div>
      </div>

      {error && (
        <div className="error-message">
          {error}
          <button 
            onClick={() => setError('')} 
            className="error-close"
            aria-label="Close error"
          >
            ×
          </button>
        </div>
      )}

      <form onSubmit={addTodo} className="add-todo-form">
        <input
          type="text"
          value={newTodo}
          onChange={(e) => setNewTodo(e.target.value)}
          placeholder="Add a new todo..."
          className="todo-input"
          disabled={isLoading}
        />
        <button 
          type="submit" 
          className="add-btn"
          disabled={isLoading || !newTodo.trim()}
        >
          {isLoading ? 'Adding...' : 'Add'}
        </button>
      </form>

      <div className="filter-buttons">
        <button
          onClick={() => setFilter('all')}
          className={filter === 'all' ? 'active' : ''}
          disabled={isLoading}
        >
          All ({todos.length})
        </button>
        <button
          onClick={() => setFilter('active')}
          className={filter === 'active' ? 'active' : ''}
          disabled={isLoading}
        >
          Active ({activeCount})
        </button>
        <button
          onClick={() => setFilter('completed')}
          className={filter === 'completed' ? 'active' : ''}
          disabled={isLoading}
        >
          Completed ({completedCount})
        </button>
      </div>

      {isLoading && todos.length === 0 ? (
        <div className="loading">Loading todos...</div>
      ) : (
        <div className="todos-list">
          {filteredTodos.length === 0 ? (
            <div className="no-todos">
              {filter === 'all' ? 'No todos yet. Add one above!' : 
               filter === 'active' ? 'No active todos.' : 
               'No completed todos.'}
            </div>
          ) : (
            filteredTodos.map(todo => (
              <div 
                key={todo._id} 
                className={`todo-item ${todo.completed ? 'completed' : ''} ${isLoading ? 'loading' : ''}`}
              >
                <input
                  type="checkbox"
                  checked={todo.completed}
                  onChange={() => toggleTodo(todo._id)}
                  className="todo-checkbox"
                  disabled={isLoading}
                />
                
                {editId === todo._id ? (
                  <input
                    type="text"
                    value={editText}
                    onChange={(e) => setEditText(e.target.value)}
                    onKeyPress={(e) => {
                      if (e.key === 'Enter') {
                        saveEdit(todo._id);
                      } else if (e.key === 'Escape') {
                        cancelEdit();
                      }
                    }}
                    className="edit-input"
                    autoFocus
                  />
                ) : (
                  <span 
                    className="todo-text"
                    onDoubleClick={() => startEdit(todo)}
                  >
                    {todo.title}
                  </span>
                )}

                <div className="todo-actions">
                  {editId === todo._id ? (
                    <>
                      <button
                        onClick={() => saveEdit(todo._id)}
                        className="save-btn"
                        disabled={isLoading || !editText.trim()}
                      >
                        Save
                      </button>
                      <button
                        onClick={cancelEdit}
                        className="cancel-btn"
                        disabled={isLoading}
                      >
                        Cancel
                      </button>
                    </>
                  ) : (
                    <>
                      <button
                        onClick={() => startEdit(todo)}
                        className="edit-btn"
                        disabled={isLoading}
                      >
                        Edit
                      </button>
                      <button
                        onClick={() => deleteTodo(todo._id)}
                        className="delete-btn"
                        disabled={isLoading}
                      >
                        ×
                      </button>
                    </>
                  )}
                </div>
              </div>
            ))
          )}
        </div>
      )}
    </div>
  );
}

export default TodoList;
