import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaSyncAlt, FaListUl } from 'react-icons/fa';

const TodoList = () => {
  const [todos, setTodos] = useState([]);
  const [newTitle, setNewTitle] = useState('');
  const [newDescription, setNewDescription] = useState('');
  const [filter, setFilter] = useState('all');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [editingId, setEditingId] = useState(null);
  const [editTitle, setEditTitle] = useState('');
  const [editDescription, setEditDescription] = useState('');

  const fetchTodos = async () => {
    setLoading(true);
    try {
      const response = await axios.get('/api/todos');
      setTodos(response.data);
    } catch (error) {
      setError('Failed to fetch todos.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTodos();
  }, []);

  const handleAddTodo = async (e) => {
    e.preventDefault();
    if (!newTitle.trim()) return;

    try {
      const response = await axios.post('/api/todos', {
        title: newTitle,
        description: newDescription
      });
      setTodos([...todos, response.data]);
      setNewTitle('');
      setNewDescription('');
    } catch {
      setError('Failed to add todo.');
    }
  };

  const handleUpdateTodo = async (id) => {
    try {
      const response = await axios.put(`/api/todos/${id}`, {
        title: editTitle,
        description: editDescription
      });

      setTodos(todos.map(todo => (todo.id === id ? response.data : todo)));
      setEditingId(null);
    } catch {
      setError('Failed to update todo.');
    }
  };

  const filteredTodos = todos.filter(todo => {
    if (filter === 'active') return !todo.completed;
    if (filter === 'completed') return todo.completed;
    return true;
  });

  const activeCount = todos.filter(todo => !todo.completed).length;
  const completedCount = todos.filter(todo => todo.completed).length;

  return (
    <div className="todo-list-container">
      <div className="todo-header">
        <h2>My Todos</h2>
        <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
          <span className="todo-stats">
            <span>Active: {activeCount}</span>
            <span>Completed: {completedCount}</span>
          </span>
          <button
            onClick={fetchTodos}
            style={{ border: '1px solid #ccc', padding: '0.5rem', borderRadius: '4px', cursor: 'pointer' }}
            title="Refresh list"
          >
            <FaSyncAlt />
          </button>
          <button
            onClick={() => setFilter('all')}
            style={{ border: '1px solid #ccc', padding: '0.5rem', borderRadius: '4px', cursor: 'pointer' }}
            title="View List"
          >
            <FaListUl />
          </button>
        </div>
      </div>

      <form onSubmit={handleAddTodo} className="add-todo-form" style={{ display: 'flex', gap: '1rem', marginBottom: '1rem' }}>
        <div style={{ flex: 1 }}>
          <label htmlFor="title" style={{ display: 'block', marginBottom: '0.25rem', fontWeight: '500' }}>Title</label>
          <input
            id="title"
            type="text"
            className="todo-input"
            value={newTitle}
            onChange={(e) => setNewTitle(e.target.value)}
            placeholder="Todo title..."
          />
        </div>

        <div style={{ flex: 1 }}>
          <label htmlFor="description" style={{ display: 'block', marginBottom: '0.25rem', fontWeight: '500' }}>Description</label>
          <input
            id="description"
            type="text"
            className="todo-input"
            value={newDescription}
            onChange={(e) => setNewDescription(e.target.value)}
            placeholder="Todo description..."
          />
        </div>

        <button type="submit" className="add-btn">Add</button>
      </form>

      <div className="filters" style={{ marginBottom: '1rem' }}>
        <button onClick={() => setFilter('all')} className={filter === 'all' ? 'active' : ''}>All</button>
        <button onClick={() => setFilter('active')} className={filter === 'active' ? 'active' : ''}>Active</button>
        <button onClick={() => setFilter('completed')} className={filter === 'completed' ? 'active' : ''}>Completed</button>
      </div>

      <div className="todo-items">
        {loading ? (
          <p>Loading...</p>
        ) : filteredTodos.map(todo => (
          <div key={todo.id} className="todo-item">
            {editingId === todo.id ? (
              <>
                <input
                  value={editTitle}
                  onChange={(e) => setEditTitle(e.target.value)}
                  placeholder="Edit title"
                />
                <input
                  value={editDescription}
                  onChange={(e) => setEditDescription(e.target.value)}
                  placeholder="Edit description"
                />
                <button onClick={() => handleUpdateTodo(todo.id)} className="save-btn">Save</button>
                <button onClick={() => setEditingId(null)} className="cancel-btn">Cancel</button>
              </>
            ) : (
              <>
                <div className="todo-text" onDoubleClick={() => {
                  setEditingId(todo.id);
                  setEditTitle(todo.title);
                  setEditDescription(todo.description);
                }}>
                  <div className="todo-title"><strong>Title:</strong> {todo.title}</div>
                  {todo.description && <div className="todo-description"><strong>Description:</strong> {todo.description}</div>}
                  <div className="todo-meta">
                    {todo.createdAt && <span><strong>Created:</strong> {new Date(todo.createdAt).toLocaleString()}</span>}
                    {todo.updatedAt && <span><strong> Updated:</strong> {new Date(todo.updatedAt).toLocaleString()}</span>}
                  </div>
                </div>
              </>
            )}
          </div>
        ))}
      </div>

      {error && <div className="error-message">{error}</div>}
    </div>
  );
};

export default TodoList;
